import './App.css';
import { useEffect, useState } from 'react';
import video from './food.mp4';
import MyRecipesComponent from './MyResipesComponent';


function App() {

const MY_ID = "a47436f0";
const MY_KEY = "f4a6b75ab641cafd56a069a5ef4cffc7";

const [mySearch, setMySearch] = useState('');
const [myRecipes, setMyRecipes] = useState([]);
const [worldSubmitted, setWorldSubmitted] = useState('avocado');






useEffect(() => {
  const getRecipe = async () => {
  const response = await fetch(`https://api.edamam.com/api/recipes/v2?type=public&q=${wordSubmitted}&app_id=${MY_ID}&app_key=${MY_KEY}`);
  const data = await response.json();
  console.log(data.hits);
  setMyRecipes(data.hits);
  }
  getRecipe()
}, [wordSubmitted])

const myRecipeSearch = (e) => {
  setMySearch(e.target.value);
}

const formSubmit = (e) => {
  e.preventDefault();
  setWordSubmitted(mySearch);
}

const searchBtn = () => {
setWordSubmitted(mySearch);
}


return (
  <div>
      <div className='wrapper'>
<video autoPlay muted loop>
  <source src={video} type="video/mp4"/>
</video>
<div className='container'>
  <h1>Find your recipe</h1>
</div>
<div className='container'>
  <form className='form' onSubmit={formSubmit}>
    <div className='form_input'>
      <img className='icon' src='https://img.freepik.com/free-vector/realistic-illustration-magnifier-with-black-plastic-handle_1441-2711.jpg?size=338&ext=jpg&uid=R72558612&ga=GA1.2.568272328.1666644729&semt=sph' alt='icons' className='icons' />/>
      <input onChange={myRecipeSearch} value={mySearch} placeholder='Search 2M+ recipes across the web..'/>
    </div>
  </form>
</div>
<div className='recipes_container'>
  {myRecipes.map((element, index) => (
    <MyRecipesComponent
      key={index}
      id={index+ 1}
      label={element.recipe.label}
      image={element.recipe.image}
      cuisineType={element.recipe.cuisineType}
      source={element.recipe.source}
      link={element.recipe.url}
      // calories={element.recipe.calories}
    />
    ))}
</div>
</div>
  </div>
)
}
