import './App.css';
import { useEffect, useState } from 'react';
import video from './food.mp4';
import MyRecipesComponent from './MyResipesComponent';


function App() {

const MY_ID = "a47436f0";
const MY_KEY = "f4a6b75ab641cafd56a069a5ef4cffc7";

const [mySearch, setMySearch] = useState('');
const [myRecipes, setMyRecipes] = useState([]);
const [worldSubmitted, setWorldSubmitted] = useState('avocado');

useEffect(()=>{
  const rec=async()=>{
    const response = await fetch(`https://api.edamam.com/api/recipes/v2?type=public&q=${wordSubmitted}&app_id=${MY_ID}&app_key=${MY_KEY}`)
    const data= await response.json();
    setMyRecepies(data.hits)
    console.log(data.hits)
  }
  rec()
},[wordSubmitted])
const myResipeSearch = (e) =>{
  console.log(e.target.value)
  setMySearch (e.target.value)
}

const finalSearch =(e) =>{
  e.preventDefault();
  setWorldSubmitted (mySearch)
}

  return(
<div className="App">

<div className="container">
<video autoPlay muted loop><source src={video} type="video/mp4" /></video>

<h1>Find a Recipe</h1>
</div>

<div className="container">
  <form onSubmit={finalSearch} >
    <input className='search' placeholder='Search...' onChange={myResipeSearch} value={mySearch} ></input>

  </form>
</div>

<div className="container">
<button>
  <img src='https://img.freepik.com/free-vector/realistic-illustration-magnifier-with-black-plastic-handle_1441-2711.jpg?size=338&ext=jpg&uid=R72558612&ga=GA1.2.568272328.1666644729&semt=sph' alt='icons' className='icons' />
</button>
</div>

<div>
  {myRecipes.map(element =>(
   <MyRecipesComponent
   label={element.resipe.label} 
   image={element.resipe.image} 
   calories={element.resipe.calories} 
   ingredients={element.resipe.ingredients} 
   />
  ))}
</div>

</div>
);
}

export default App;
